mapper.select:
SELECT *
FROM MENU
WHERE ID = #{menuId}
  AND STORE_ID = #{storeId}

mapper.update:
UPDATE MENU
SET (NAME, PRICE, PHOTO, CATEGORY)
        = (#{name}, #{price}, #{photo}, #{category})
WHERE ID = #{id}
  AND STORE_ID = #{storeId}

mapper.delete:
DELETE
FROM MENU
WHERE ID = #{menuId}
  AND STORE_ID = #{storeId}
