mapper.select:
SELECT *
FROM ORDER_ITEM
WHERE ACCOUNT_ID = #{userId}
  AND ORDER_CODE BETWEEN 100 AND 109
  AND STORE_ID = #{storeId}

mapper.update:
UPDATE ORDER_ITEM
SET (TABLE_ID, RESERVATION_TIME, ORDER_CODE)
        = (#{tableId}, #{reservationTime}, #{orderCode})
WHERE ID = #{id}
  AND ACCOUNT_ID = #{accountId}
