mapper.select:
SELECT *
FROM MENU
WHERE ID = #{menuId}
  AND STORE_ID = #{storeId}

mapper.update:
UPDATE MENU
SET (PHOTO, NAME, PRICE, CATEGORY)
        = (#{photo}, #{name}, #{price}, #{category})
WHERE ID = #{id}
  AND STORE_ID = #{storeId}

mapper.delete:
DELETE
FROM MENU
WHERE ID = #{menuId}
  AND STORE_ID = #{storeId}
